00327   channel     = sdp_data_alloc(SDP_UINT8, &rfcomm_channel);
00328   rfcomm_list = sdp_list_append( 0, &rfcomm_uuid );
00329   sdp_list_append( rfcomm_list, channel );
00330   sdp_list_append( proto_list, rfcomm_list );
