00338 
00339   // connect to the local SDP server, register the service record, and
00340   // disconnect
00341   sdp_record_register(session, &record, 0);
00342 
00343   // cleanup
00344   sdp_data_free(channel);
00345   sdp_list_free(l2cap_list        , 0);
00346   sdp_list_free(rfcomm_list       , 0);
