00707                                                                 rem_addr.rc_channel = (uint8_t) diostream->config->GetRemoteChannel();
00708 
00709                                                                 { XBUFFER charstr;
00710                                                                   
00711                                                                   remoteMACstring.ConvertToASCII(charstr);                                                                  
00712                                                                   str2ba(charstr.GetPtrChar(), &rem_addr.rc_bdaddr);                                                                  
00713                                                                 }
00714 
00715                                                                 diostream->SetIsRefusedConnection(false);
00716 
00717                                                                 fcntl(diostream->handlesocket, F_SETFL, fcntl(diostream->handlesocket, F_GETFL,0) | O_NONBLOCK);
00718 
00719                                                                 connect(diostream->handlesocket, (struct sockaddr *)&rem_addr, sizeof(rem_addr));
00720 
00721                                                               }
00722                                                           }
00723                                                           break;
00724 
00725               case DIOLINUXBTFSMSTATE_CONNECTED         : diostream->SetEvent(DIOLINUXBTFSMEVENT_WAITINGTOREAD);
00726                                                           break;
00727 
00728               case DIOLINUXBTFSMSTATE_WAITINGTOREAD     : break;
00729 
00730               case DIOLINUXBTFSMSTATE_SENDINGDATA       : break;
00731 
00732               case DIOLINUXBTFSMSTATE_DISCONNECTING     : if(diostream->config->GetPIN()->GetSize())
00733                                                             {
00734                                                               XSTRING localMACstring;
00735                                                               XSTRING remoteMACstring;
00736 
00737                                                               diostream->config->GetLocalMAC()->GetXString(localMACstring);
00738                                                               diostream->config->GetRemoteMAC()->GetXString(remoteMACstring);
00739 
00740                                                               diostream->ManagementOfPIN(false, localMACstring, remoteMACstring, (*diostream->config->GetPIN()));
00741                                                             }
00742 
00743                                                           diostream->threadconnection->Run(false);
00744                                                           diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00745                                                           break;
00746             }
00747         }
00748     }
00749 }
