00252   hci_le_set_scan_enable(hcisocket, 0x00, 0, 1000);
00253 
00254   // restore original filter
00255   setsockopt(hcisocket, SOL_HCI, HCI_FILTER, &old_hcifilter, sizeof(old_hcifilter));
00256 
00257   close(hcisocket);
00258 
00259   hcisocket = -1;
00260 
00261   // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[LE Remote Search] stop...."));
