00290 int DIOLINUXSTREAMBLUETOOTHREMOTEENUMDEVICES::IsReadyConnect(int socket)
00291 {
00292   struct timeval  tv;
00293   int             rc;
00294   fd_set          fdr;
00295   fd_set          fdw;
00296   fd_set          fds;
00297 
00298   if(socket==-1) return -1;
00299 
00300   FD_ZERO(&fdr);
00301   FD_ZERO(&fdw);
00302   FD_ZERO(&fds);
00303 
00304   FD_SET((unsigned int)socket,&fdr);
00305   FD_SET((unsigned int)socket,&fdw);
00306   FD_SET((unsigned int)socket,&fds);
00307 
00308   tv.tv_sec  = 0;
00309   tv.tv_usec = 100;
00310 
00311   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00312   if(rc==-1) return -1;
00313   if(rc== 2) return -1;
00314 
00315   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00316   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00317   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00318 
00319   if(status3) return -1;
00320   if(((status1) || (status2)) && (rc==1))  return  1;
00321 
00322   return 0;
00323 }
