00611 void DIOLINUXSTREAMBLUETOOTHREMOTEENUMDEVICES::ThreadEnumDevices(void* param)
00612 {
00613   DIOLINUXSTREAMBLUETOOTHREMOTEENUMDEVICES* enumeration = (DIOLINUXSTREAMBLUETOOTHREMOTEENUMDEVICES*)param;
00614   if(!enumeration) return;
00615 
