00619         {
00620           case DIOLINUXBTENUMFSMSTATE_NONE            : break;
00621 
00622           case DIOLINUXBTENUMFSMSTATE_SEARCHMAC       : enumeration->SetEvent(DIOLINUXBTENUMFSMEVENT_SEARCHSERVICES);
00623                                                         break;
00624 
00625           case DIOLINUXBTENUMFSMSTATE_SEARCHSERVICES  : enumeration->SetEvent(DIOLINUXBTENUMFSMEVENT_SEARCHEND);
00626                                                         break;
00627 
00628           case DIOLINUXBTENUMFSMSTATE_SEARCHEND       : break;
00629 
00630 
00631         }
00632     }
00633    else
00634     {
00635       if(enumeration->GetEvent()<DIOLINUXBTENUM_LASTEVENT)
00636         {
00637           enumeration->CheckTransition();
00638 
00639           switch(enumeration->GetCurrentState())
00640             {
00641               case DIOLINUXBTENUMFSMSTATE_NONE            : break;
00642 
00643               case DIOLINUXBTENUMFSMSTATE_SEARCHMAC       : enumeration->ScanDevices(&enumeration->devices);
00644                                                             break;
00645 
00646               case DIOLINUXBTENUMFSMSTATE_SEARCHSERVICES  : enumeration->ScanDevicesServices(&enumeration->devices);
00647                                                             break;
00648 
00649               case DIOLINUXBTENUMFSMSTATE_SEARCHEND       : break;
00650            }
00651         }
00652     }
