00171 bool DIOLINUXSTREAMICMP::Open()
00172 {
00173   if(!threadconnection)   return false;
00174 
00175   if(!inbuffer)           return false;
00176   if(!outbuffer)          return false;
00177 
00178   if(!config->IsServer())
00179     {
00180       if(config->GetRemoteURL()->IsEmpty()) 
00181         {
00182           return false;   
00183         }
00184 
00185       if(config->GetRemoteURL()->IsAURL())
00186         {
00187           config->GetRemoteURL()->ResolveURL((*config->GetResolvedRemoteURL()));
00188         }
00189        else 
00190         {
00191           config->GetResolvedRemoteURL()->Set(config->GetRemoteURL()->Get());      
00192         }
00193     }
00194 
00195   SetEvent(DIOLINUXICMPFSMEVENT_GETTINGCONNECTION);
00196 
00197   status = DIOSTREAMSTATUS_GETTINGCONNECTION;
00198 
00199   ResetXBuffers();
00200 
00201   ResetConnectionStatistics();
00202 
00203   return threadconnection->Ini();
00204 }
