00216 bool DIOLINUXSTREAMICMP::Disconnect()
00217 {
00218   if((GetConnectStatus()==DIOSTREAMSTATUS_GETTINGCONNECTION)||
00219      (GetConnectStatus()==DIOSTREAMSTATUS_CONNECTED))
00220      {
00221        SetEvent(DIOLINUXICMPFSMEVENT_DISCONNECTING);
00222 
00223        while(GetConnectStatus()!=DIOSTREAMSTATUS_DISCONNECTED)
00224         {
00225           usleep(10000);
00226         }
00227      }
00228 
00229   return true;
