00270 int DIOLINUXSTREAMICMP::IsReadyConnect(int socket)
00271 {
00272   struct timeval  tv;
00273   int             rc;
00274   fd_set          fdr;
00275   fd_set          fdw;
00276   fd_set          fds;
00277 
00278   if(socket==-1) return -1;
00279 
00280   FD_ZERO(&fdr);
00281   FD_ZERO(&fdw);
00282   FD_ZERO(&fds);
00283 
00284   FD_SET(socket,&fdr);
00285   FD_SET(socket,&fdw);
00286   FD_SET(socket,&fds);
00287 
00288   tv.tv_sec  = 0;
00289   tv.tv_usec = 100;
00290 
00291   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00292   if(rc==-1) return -1;
00293 
00294   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00295   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00296   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00297 
00298   if(config->IsServer())
00299     {
00300       if(status1 || status2 ) return  1;
00301       if(status3)             return -1;
00302     }
00303    else
00304     {
00305       if((!status1) && status2) return  1;
00306       if(status3)               return -1;
00307     }
00308 
00309   return 0;
00310 }
