00764           case DIOLINUXTCPIPFSMSTATE_SENDINGDATA        : break;
00765 
00766           case DIOLINUXTCPIPFSMSTATE_DISCONNECTING      : break;
00767 
00768         }
00769     }
00770    else
00771     {
00772       if(diostream->GetEvent()<DIOLINUXTCPIP_LASTEVENT)
00773         {
00774           diostream->CheckTransition();
00775 
00776           switch(diostream->GetCurrentState())
00777             {
00778               case DIOLINUXTCPIPFSMSTATE_NONE               : break;
00779 
00780               case DIOLINUXTCPIPFSMSTATE_GETTINGCONNECTION   : if(diostream->enumservers)
00781                                                                 {
00782                                                                   if(diostream->enumservers->GetXTimer()) diostream->enumservers->GetXTimer()->AddSeconds(DIOSTREAMTCPIP_TIMEOUTENUMSERVERSIGNAL);
00783                                                                 }
00784 
00785                                                               if(diostream->config->IsServer())
00786                                                                 {
00787                                                                   diostream->config->SetCounterMultiServer(diostream->config->GetCounterMultiServer()+1);
00788                                                                 }
00789                                                               break;
00790 
00791               case DIOLINUXTCPIPFSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_CONNECTED);
00792                                                                 xevent.SetDIOStream(diostream);
00793                                                                 diostream->PostEvent(&xevent);
00794 
00795                                                                 diostream->SetEvent(DIOLINUXTCPIPFSMEVENT_WAITINGTOREAD);
00796                                                               }
00797                                                               break;
00798 
00799               case DIOLINUXTCPIPFSMSTATE_WAITINGTOREAD      : break;
00800 
00801               case DIOLINUXTCPIPFSMSTATE_SENDINGDATA        : break;
00802 
00803               case DIOLINUXTCPIPFSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00804                                                                 xevent.SetDIOStream(diostream);
00805 
00806                                                                 diostream->PostEvent(&xevent);
00807 
00808                                                                 diostream->threadconnection->Run(false);
00809                                                                 diostream->status       = DIOSTREAMSTATUS_DISCONNECTED;
00810                                                               }
00811                                                               break;
00812 
00813             }
00814         }
00815     }
00816 }
00817 
00818 
00827 void DIOLINUXSTREAMTCPIP::Clean()
00828 {
00829   threadconnection   = NULL;
00830   handlesocket      = -1;
00831 }
