00166 bool DIOLINUXSTREAMUART::Open()
00167 {
00168   if(!config) return false;
00169 
00170   XSTRING ttyS;
00171 
00172   if(!config->GetLocalDeviceName()->IsEmpty())
00173     {
00174       ttyS = config->GetLocalDeviceName()->Get();
00175     }
00176    else
00177     {
00178       ttyS.Format(__L("/dev/ttyS%d"),config->GetPort());
00179     }
00180 
00181   XBUFFER charstr;
00182   
00183   ttyS.ConvertToASCII(charstr);  
00184   fd= open(charstr.GetPtrChar(), O_RDWR | O_NOCTTY | O_NONBLOCK);  
00185   if(fd<0) return false;
00186 
00187   SetRTS(false);
00188   SetDTR(false);
00189 
00190   CleanBuffers();
00191 
00192   #ifdef LINUX
00193   fcntl(fd, F_SETFL, FNDELAY);
00194   #else
00195   fcntl(fd, F_SETFL, O_NONBLOCK);
00196   #endif
00197 
00198   Config();
00199 
00200   SetEvent(DIOLINUXUARTFSMEVENT_CONNECTED);
00201 
00202   status = DIOSTREAMSTATUS_CONNECTED;
00203 
00204   ResetXBuffers();
00205   ResetConnectionStatistics();
00206 
00207   return threadconnection->Ini();
00208 }
