00243           case 57600  : speed = B57600;     break;
00244           case 115200 : speed = B115200;    break;
00245         }
00246 
00247       cfsetispeed(&cfg, speed);
00248       cfsetospeed(&cfg, speed);
00249     }
00250 
00251   if((mask&DIOSTREAMUARTMASK_DATABITS ) == DIOSTREAMUARTMASK_DATABITS)
00252     {
00253       cfg.c_cflag&= ~CSIZE;
00254 
00255       switch(this->config->GetDataBits())
