00257           case DIOSTREAMUARTDATABIT_5           : cfg.c_cflag|= CS5; break;
00258           case DIOSTREAMUARTDATABIT_6           : cfg.c_cflag|= CS6; break;
00259           case DIOSTREAMUARTDATABIT_7           : cfg.c_cflag|= CS7; break;
00260           case DIOSTREAMUARTDATABIT_8           : cfg.c_cflag|= CS8; break;
00261         }
00262     }
00263 
00264   if((mask&DIOSTREAMUARTMASK_PARITY) == DIOSTREAMUARTMASK_PARITY)
00265     {
00266       cfg.c_iflag  = (IGNBRK| IGNPAR);
