00308           case DIOSTREAMUARTSTOPBITS_TWO          : cfg.c_cflag |=  CSTOPB;   break;
00309         }
00310     }
00311 
00312   if((mask&DIOSTREAMUARTMASK_FLOWCONTROL) == DIOSTREAMUARTMASK_FLOWCONTROL)
00313     {
00314       switch(config->GetFlowControl())
00315         {
