00555 bool DIOLINUXSTREAMUART::SetRTS(bool on)
00556 {
00557   if(fd<0) return false;
00558 
00559   int _status;
00560 
00561   ioctl(fd, TIOCMGET, &_status); // get the serial port status
00562 
00563   if(on) _status &= ~TIOCM_RTS; else _status |= TIOCM_RTS;
00564 
00565   ioctl(fd, TIOCMSET, &_status); // set the serial port status
00566 
00567   return true;
00568 }
