00627 void DIOLINUXSTREAMUART::ThreadConnection(void* param)
00628 {
00629   DIOLINUXSTREAMUART* diostream = (DIOLINUXSTREAMUART*)param;
00630   if(!diostream) return;
00631 
00632   if(diostream->GetEvent()==DIOLINUXUARTFSMEVENT_NONE) // No hay nuevos Eventos
00633     {
00634       switch(diostream->GetCurrentState())
00635         {
00636           case DIOLINUXUARTFSMSTATE_NONE                    : break;
00637 
00638           case DIOLINUXUARTFSMSTATE_CONNECTED               : break;
00639 
00640           case DIOLINUXUARTFSMSTATE_WAITINGTOREAD           : {
00641 
00642                                                                 if(!diostream->IsBlockRead())
00643                                                                   {
00644                                                                     XBYTE buffer[DIOSTREAM_MAXBUFFER];
00645 
00646                                                                     XDWORD br = diostream->ReadDirect(buffer, DIOSTREAM_MAXBUFFER);
00647                                                                     if(br)
00648                                                                       {
00649                                                                         //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(2), (XBYTE*)diostream->buffer, (int)br);
00650                                                                         diostream->inbuffer->Add(buffer, br);
00651                                                                       }
00652                                                                   }
00653 
00654                                                                 if(!diostream->IsBlockWrite())
00655                                                                   {
00656                                                                     int esize = diostream->outbuffer->GetSize();
00657                                                                     if(esize)
00658                                                                       {
00659                                                                         if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00660 
00661                                                                         diostream->outbuffer->SetBlocked(true);
00662                                                                         XDWORD bw = diostream->WriteDirect(diostream->outbuffer->Get(), esize);
00663                                                                         diostream->outbuffer->SetBlocked(false);
00664 
00665                                                                         if(bw)
00666                                                                           {
00667                                                                             //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(4), (XBYTE*)diostream->buffer, (int)bw);
00668                                                                             diostream->outbuffer->Extract(NULL, 0, bw);
00669                                                                           }
00670                                                                       }
00671                                                                   }
00672                                                               }
00673                                                               break;
00674 
00675           case DIOLINUXUARTFSMSTATE_DISCONNECTING           : break;
00676 
00677         }
00678     }
00679    else
00680     {
00681       if(diostream->GetEvent()<DIOLINUXUART_LASTEVENT)
00682         {
00683           diostream->CheckTransition();
00684 
00685           switch(diostream->GetCurrentState())
00686             {
00687               case DIOLINUXUARTFSMSTATE_NONE                : break;
00688 
00689               case DIOLINUXUARTFSMSTATE_CONNECTED           : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00690                                                                 xevent.SetDIOStream(diostream);
00691                                                                 diostream->PostEvent(&xevent);
00692 
00693                                                                 diostream->SetEvent(DIOLINUXUARTFSMEVENT_WAITINGTOREAD);
00694                                                               }
00695                                                               break;
00696 
00697               case DIOLINUXUARTFSMSTATE_WAITINGTOREAD       : break;
00698 
00699               case DIOLINUXUARTFSMSTATE_DISCONNECTING       : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00700                                                                 xevent.SetDIOStream(diostream);
00701                                                                 diostream->PostEvent(&xevent);
00702 
00703                                                                 diostream->threadconnection->Run(false);
00704                                                                 diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00705                                                               }
00706                                                               break;
00707             }
00708         }
00709     }
00710 }
