00557 bool DIOLINUXWIFIMANAGERMODE::WPA_GetListRemoteAP(XVECTOR<XSTRING*>& nameAPs)
00558 { 
00559   XSTRING   prefixcommand;
00560   XSTRING   command;
00561   XSTRING   output;
00562   bool      status;
00563 
00564 
00565   nameAPs.DeleteContents();
00566   nameAPs.DeleteAll();
00567 
00568   prefixcommand.Format(DIOLINUXWIFIMANAGERMODE_PREFIXCOMMAND, netinterface.Get());
00569 
00570   command.Format(__L("%s scan") , prefixcommand.Get());     
00571   status = WPA_CLI_Command(command, output);
00572   if(!status) return false;
00573 
00574   GEN_XSLEEP.Seconds(3);
00575 
00576   command.Format(__L("%s scan_results") , prefixcommand.Get());     
00577   status = WPA_CLI_Command(command, output);
00578 
00579   int indexfr =  output.Find(__L("\n"), true);
00580   if(indexfr == XSTRING_NOTFOUND) return false;
00581 
00582   indexfr++;
00583 
00584   int index      = 0;
00585   int indexstart = 0;
00586 
00587   do{
00588       index =  output.Find(__L("\n"), true, indexfr);
00589       if(index == XSTRING_NOTFOUND ) break;
00590 
00591       for(int c=index; c>0 ; c--)
00592         {
00593           if(output.Get()[c] == __C(']'))  
00594             {
00595               indexstart = c;
00596               break;
00597             }               
00598         }
00599 
00600       indexfr = index+1;
00601 
00602       if(indexstart)
00603         {
00604           XSTRING* newstr = new XSTRING();
00605           if(newstr)
00606             {
00607               output.Copy(indexstart+1, index, (*newstr));
00608               newstr->DeleteCharacter(__C('\t'), XSTRINGCONTEXT_FROM_FIRST);
00609               
00610               nameAPs.Add(newstr);                 
00611             }
00612         }
00613 
00614     } while(index != XSTRING_NOTFOUND);
00615 
00616   return true;
00617 }
