00729 bool DIOPROTOCOL_FILE::ReadBlock(XBYTE* buffer)
00730 {
00731   if(!sourcename[0]) return false;
00732 
00733   if(!xfile->Open(sourcename)) return false;
00734 
00735   bool status = false;
00736 
00737   size = xfile->GetSize();
00738   if(size)
00739     {
00740       xfile->SetPosition(position);
00741 
00742       xfile->Read(buffer, &sizeblock);
00743       if(sizeblock)
00744         {
00745           xfile->GetPosition(position);
00746           if(size) percent = (XBYTE)(position*100/size);
00747 
00748           status = true;
00749         }
00750     }
00751 
00752   xfile->Close();
00753 
00754   return status;
00755 }
00756 
00757 
00769 bool DIOPROTOCOL_FILE::WriteBlock(XBYTE* buffer)
00770 {
00771   if(!xfile)         return false;
00772   if(!targetname[0]) return false;
00773 
00774   if(position)
00775     {
00776       if(!xfile->Open(targetname,false))
00777         {
00778           if(!xfile->Create(targetname)) return false;
00779         }
00780     }
00781    else
00782     {
00783       if(!xfile->Create(targetname)) return false;
00784     }
00785 
00786   bool status = false;
00787 
00788   if(sizeblock)
00789     {
00790       xfile->SetPosition((XDWORD)XFILE_SEEKEND);
00791 
00792       status = xfile->Write(buffer,sizeblock);
00793       if(status)
00794         {
00795           xfile->GetPosition(position);
00796           if(size) percent = (XBYTE)(position*100/size);
00797         }
00798     }
00799 
00800   xfile->Close();
00801 
00802   return status;
00803 }
