00882 DIOPROTOCOL::DIOPROTOCOL(DIOSTREAM* diostream)
00883 {
00884   Clean();
00885 
00886   this->diostream   = diostream;
00887 
00888   answers.SetIsMulti(true);
00889   asks.SetIsMulti(true);
00890 
00891   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_COMMAND);
00892   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_FILEBLOCK);
00893   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
00894 
00895   GEN_XFACTORY_CREATE(xdatetime         , CreateDateTime())
00896   GEN_XFACTORY_CREATE(xtimer            , CreateTimer())
00897   GEN_XFACTORY_CREATE(watchdog          , CreateTimer())
00898   GEN_XFACTORY_CREATE(xdir              , Create_Dir())
00899   GEN_XFACTORY_CREATE(timerfileprogress , CreateTimer())
00900 
00901   AddCommand(DIOPROTOCOL_CMDTYPE_SENDISINITSERVERPROTOCOL   , __L("O")                , __L("O")            , RCV_SendIsInitServerProtocol      , __L("SendIsInitServerProtocol")   );
00902   AddCommand(DIOPROTOCOL_CMDTYPE_GETPROTOCOLVERSION         , __L("")                 , __L("WWW")          , RCV_GetProtocolVersion            , __L("GetProtocolVersion")         );
00903   AddCommand(DIOPROTOCOL_CMDTYPE_GETAPPLICATIONVERSION      , __L("")                 , __L("WWW")          , RCV_GetApplicationVersion         , __L("GetApplicationVersion")      );
00904   AddCommand(DIOPROTOCOL_CMDTYPE_GETAPPLICATIONNAME         , __L("")                 , __L("S")            , RCV_GetApplicationName            , __L("GetApplicationName")         );
00905   AddCommand(DIOPROTOCOL_CMDTYPE_PING                       , __L("")                 , __L("O")            , RCV_Ping                          , __L("Ping")                       );
00906   AddCommand(DIOPROTOCOL_CMDTYPE_GETDATETIME                , __L("")                 , __L("S")            , RCV_GetDateTime                   , __L("GetDateTime")                );
00907   AddCommand(DIOPROTOCOL_CMDTYPE_SETDATETIME                , __L("S")                , __L("O")            , RCV_SetDateTime                   , __L("SetDateTime")                );
00908   AddCommand(DIOPROTOCOL_CMDTYPE_INITFILEBLOCK              , __L("SSBD")             , __L("B")            , RCV_InitFileBlock                 , __L("FileInit")                   );
00909   AddCommand(DIOPROTOCOL_CMDTYPE_GETFILEBLOCK               , __L("")                 , __L("BDDDBAX")      , RCV_GetFileBlock                  , __L("GetFileBlock")               );
00910   AddCommand(DIOPROTOCOL_CMDTYPE_SENDFILEBLOCK              , __L("BDDDBAX")          , __L("B")            , RCV_SendFileBlock                 , __L("SendFileBlock")              );
00911   AddCommand(DIOPROTOCOL_CMDTYPE_DELETEFILE                 , __L("SB")               , __L("O")            , RCV_DeleteFile                    , __L("DeleteFile")                 );
00912   AddCommand(DIOPROTOCOL_CMDTYPE_MAKEDIR                    , __L("S")                , __L("O")            , RCV_MakeDir                       , __L("MakeDir")                    );
00913   AddCommand(DIOPROTOCOL_CMDTYPE_DELETEDIR                  , __L("SB")               , __L("O")            , RCV_DeleteDir                     , __L("DeleteDir")                  );
00914   AddCommand(DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT         , __L("SS")               , __L("SBDD")         , RCV_GetDirElement                 , __L("GetFirstDirElement")         );
00915   AddCommand(DIOPROTOCOL_CMDTYPE_GETNEXTDIRELEMENT          , __L("")                 , __L("SBDD")         , RCV_GetDirElement                 , __L("GetNextDirElement")          );
00916   #ifdef DIO_ALERTS_ACTIVE
00917   AddCommand(DIOPROTOCOL_CMDTYPE_SENDALERT                  , __L("SSDDDDBSSS")       , __L("B")            , RCV_SendAlert                     , __L("SendAlert")                  );
00918   #endif
00919 
00920   fileprogress = new DIOPROTOCOL_FILE();
00921   if(!fileprogress) return;
00922 
00923   GEN_XFACTORY_CREATE(xmutexanswers, Create_Mutex())
00924   if(!xmutexanswers) return;
00925 
00926   GEN_XFACTORY_CREATE(xmutexasks, Create_Mutex())
00927   if(!xmutexasks) return;
00928 
00929   GEN_XFACTORY_CREATE(xmutexcommands, Create_Mutex())
00930   if(!xmutexcommands) return;
00931 
00932   GEN_XFACTORY_CREATE(xmutexreads,Create_Mutex())
00933   if(!xmutexreads) return;
00934 
00935   GEN_XFACTORY_CREATE(xmutexwrites, Create_Mutex())
00936   if(!xmutexwrites) return;
00937 
00938   GEN_XFACTORY_CREATE(xmutexfilesprogress, Create_Mutex())
00939   if(!xmutexfilesprogress) return;
00940 
00941   xthreadreceived = CREATEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, __L("DIOPROTOCOL::DIOPROTOCOL received"),ThreadReceived,(void*)this);
00942   if(xthreadreceived)
00943     {
00944       if(!xthreadreceived->Ini()) return;
00945     }
00946 
00947   xthreadasks = CREATEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, __L("DIOPROTOCOL::DIOPROTOCOL ask"), ThreadAsks, (void*)this);
00948   if(xthreadasks)
00949     {
00950       if(!xthreadasks->Ini()) return;
00951     }
00952 }
