00963 DIOPROTOCOL::~DIOPROTOCOL()
00964 {
00965   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_COMMAND);
00966   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_FILEBLOCK);
00967   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
00968 
00969   doexit = true;
00970 
00971   if(xthreadreceived)
00972     {
00973       xthreadreceived->End();
00974       DELETEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, xthreadreceived);
00975       xthreadreceived = NULL;
00976     }
00977 
00978   if(xthreadasks)
00979     {
00980       xthreadasks->End();
00981       DELETEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, xthreadasks);
00982       xthreadasks = NULL;
00983     }
00984 
00985   DeleteAllAnswers();
00986   DeleteAllAsks();
00987 
00988   DeleteAllCommands();
00989 
00990   GEN_XFACTORY.Delete_Mutex(xmutexanswers);
00991   GEN_XFACTORY.Delete_Mutex(xmutexasks);
00992   GEN_XFACTORY.Delete_Mutex(xmutexcommands);
00993   GEN_XFACTORY.Delete_Mutex(xmutexreads);
00994   GEN_XFACTORY.Delete_Mutex(xmutexwrites);
00995   GEN_XFACTORY.Delete_Mutex(xmutexfilesprogress);
00996 
00997   delete fileprogress;
00998 
00999   if(xdir)              GEN_XFACTORY.Delete_Dir(xdir);
01000   if(watchdog)          GEN_XFACTORY.DeleteTimer(watchdog);
01001   if(xtimer)            GEN_XFACTORY.DeleteTimer(xtimer);
01002   if(xdatetime)         GEN_XFACTORY.DeleteDateTime(xdatetime);
01003   if(timerfileprogress) GEN_XFACTORY.DeleteTimer(timerfileprogress);
01004 
01005   Clean();
01006 }
