01417 DIOPROTOCOL_COMMAND* DIOPROTOCOL::GetCommand(XDWORD type)
01418 {
01419   for(int c=0;c<(int)commands.GetSize();c++)
01420     {
01421       DIOPROTOCOL_COMMAND* cmd = (DIOPROTOCOL_COMMAND*)commands.Get(c);
01422       if(cmd)
01423         {
01424           if(cmd->GetType() == type) return cmd;
01425         }
01426     }
01427 
01428   return NULL;
01429 }
