01445 DIOPROTOCOL_ANSWER* DIOPROTOCOL::AddAnswer(XDWORD type, XDWORD ID, XBUFFER& xbuffer)
01446 {
01447   if(xmutexanswers) xmutexanswers->Lock();
01448 
01449   DIOPROTOCOL_ANSWER* answer = new DIOPROTOCOL_ANSWER(type, ID, xbuffer);
01450   if(answer) answers.Add(answer);
01451 
01452   if(xmutexanswers) xmutexanswers->UnLock();
01453 
01454   return answer;
01455 }
