01470 DIOPROTOCOL_ANSWER* DIOPROTOCOL::GetAnswer(XDWORD type, XDWORD ID)
01471 {
01472   if(xmutexanswers) xmutexanswers->Lock();
01473 
01474   DIOPROTOCOL_ANSWER* answer = NULL;
01475 
01476   for(XDWORD c=0; c<answers.GetSize(); c++)
01477     {
01478       answer = (DIOPROTOCOL_ANSWER*)answers.Get(c);
01479       if(answer)
01480         {
01481           if((answer->GetType() == type) && (answer->GetID()  == ID))  break; else answer = NULL;
01482         }
01483     }
01484 
01485   if(xmutexanswers) xmutexanswers->UnLock();
01486 
01487   return answer;
01488 }
01489 
01490 
01503 bool DIOPROTOCOL::DeleteAnswer(XDWORD type, XDWORD ID)
01504 {
01505   if(xmutexanswers) xmutexanswers->Lock();
01506 
01507   for(XDWORD c=0; c<answers.GetSize(); c++)
01508     {
01509       DIOPROTOCOL_ANSWER* answer = (DIOPROTOCOL_ANSWER*)answers.Get(c);
01510       if(answer)
01511         {
01512           if((answer->GetType() == type) && (answer->GetID() == ID))
01513             {
01514               answers.Delete(answer);
01515               delete answer;
01516 
01517               break;
01518             }
01519         }
01520     }
01521 
01522   if(xmutexanswers) xmutexanswers->UnLock();
01523 
01524   return true;
01525 }
01526 
