01662 
01676 int DIOPROTOCOL::ReadCommand(XDWORD& type, XDWORD& ID, XBUFFER& xbuffer)
01677 {
01678   if(!IsConnected()) return 0;
01679 
01680   XDWORD sizecmd = diostream->GetInXBuffer()->GetSize();
01681   if(sizecmd < DIOPROTOCOL_SIZEOFHEAD) return 0;
01682 
01683   XBYTE head[DIOPROTOCOL_SIZEOFHEAD];
01684 
01685   memset(head ,0 , DIOPROTOCOL_SIZEOFHEAD);
01686   diostream->GetInXBuffer()->Get(head, DIOPROTOCOL_SIZEOFHEAD);
01687 
01688   HASHCRC32 crc32;
01689   XDWORD*   headcmd         = (XDWORD*)head;
01690   XDWORD    IDhead          = 0;
01691   XDWORD    sizebuffer      = 0;
01692   XDWORD    bodycrc32value  = 0;
01693   XDWORD    headcrc32value  = 0;
01694   XDWORD    crc32value      = 0;
01695 
01696   IDhead          = headcmd[0];
01697   type            = headcmd[1];
01698   sizebuffer      = headcmd[2];
01699   ID              = headcmd[3];
01700   bodycrc32value  = headcmd[4];
01701   headcrc32value  = headcmd[5];
01702 
01703   crc32.ResetResult();
01704   crc32.Do((XBYTE*)headcmd, (DIOPROTOCOL_SIZEOFHEAD - sizeof(XDWORD)));
01705   crc32value  = crc32.GetResultCRC32();
01706 
01707   if((IDhead != DIOPROTOCOL_MSGIDHEAD) || (headcrc32value != crc32value))
01708     {
01709       return 0;
01710     }
01711 
01712   if(!diostream->WaitToFilledReadingBuffer((DIOPROTOCOL_SIZEOFHEAD + sizebuffer), 5))
01713     {
01714       return 0;
01715     }
01716 
01717   //diostream->GetInXBuffer()->Extract(NULL, 0, DIOPROTOCOL_SIZEOFHEAD);
01718   diostream->Read(head, DIOPROTOCOL_SIZEOFHEAD);
01719 
01720   if(sizebuffer)
01721     {
01722       xbuffer.Delete();
01723       xbuffer.Resize(sizebuffer);
01724 
01725     //if(diostream->GetInXBuffer()->Extract(xbuffer.Get(), 0, sizebuffer))
01726       if(diostream->Read(xbuffer.Get(), sizebuffer))
01727         {
01728           crc32.ResetResult();
01729           crc32.Do(xbuffer);
01730           crc32value  = crc32.GetResultCRC32();
01731 
01732           if(bodycrc32value == crc32value)
01733             {
01734               return (DIOPROTOCOL_SIZEOFHEAD + sizebuffer);
01735             }
01736            else
01737             {
01738               return 0;
01739             }
01740 
01741         }
01742        else
01743         {
01744           return 0;
01745         }
01746     }
01747 
01748   return DIOPROTOCOL_SIZEOFHEAD;
01749 }
01750 
01751 
01761 DIOPROTOCOL_RESULT DIOPROTOCOL::Received()
01762 {
01763   if(!IsConnected()) return DIOPROTOCOL_RESULT_NOTCONNEX;
01764 
01765   if(xmutexreads) xmutexreads->Lock();
01766 
01767   int                 size = 0;
01768   DIOPROTOCOL_RESULT  status = DIOPROTOCOL_RESULT_UNKNOWNCMD;
01769 
01770   XDWORD                type   = DIOPROTOCOL_CMDTYPE_NONE;
01771   XDWORD                ID     = 0;
01772   XBUFFER               xbuffer;
01773 
01774   size = ReadCommand(type, ID, xbuffer);
01775   if(size)
01776     {
01777       bool   isanswer = ((type&DIOPROTOCOL_CMDTYPE_ANSWER)==DIOPROTOCOL_CMDTYPE_ANSWER)?true:false;
01778       XDWORD rtype    = (type&~DIOPROTOCOL_CMDTYPE_ANSWER);
01779 
01780       /*
01781       DIOPROTOCOL_COMMAND* cmd  = GetCommand(rtype);
01782       if(cmd)
01783         {
01784          XTRACE_PRINTCOLOR(((isanswer)?1:2), __L("DIOPROTOCOL %s %s : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
01785                                                                                                     , (isanswer)?__L("Answer  "):__L("Ask     ")
01786                                                                                                     , this
01787                                                                                                     , ID
01788                                                                                                     , rtype
01789                                                                                                     , cmd->GetDescription()
01790                                                                                                     , xbuffer.GetSize());
01791         }
01792       */
01793       if(isanswer)
01794         {
01795           if(AddAnswer(rtype, ID, xbuffer)) status = DIOPROTOCOL_RESULT_OK;
01796         }
01797        else
01798         {
01799           //status = ReceivedHandle(rtype, ID, xbuffer);
01800           if(AddAsk(rtype, ID, xbuffer))    status = DIOPROTOCOL_RESULT_OK;
01801         }
01802 
01803     }
01804 
01805   if(xmutexreads) xmutexreads->UnLock();
01806 
01807   return status;
01808 }
01809 
01810 
01824 DIOPROTOCOL_RESULT DIOPROTOCOL::ReceivedHandle(XDWORD type, XDWORD ID, XBUFFER& xbuffer)
01825 {
01826   DIOPROTOCOL_RESULT status     = DIOPROTOCOL_RESULT_UNKNOWNCMD;
01827   XDWORD             param      = 0;
01828 
01829   if((type & DIOPROTOCOL_CMDTYPE_ANSWER) == DIOPROTOCOL_CMDTYPE_ANSWER) return DIOPROTOCOL_RESULT_UNKNOWNCMD;
01830 
01831   DIOPROTOCOL_COMMAND* cmd = GetCommand(type);
01832   if(!cmd)  return DIOPROTOCOL_RESULT_UNKNOWNCMD;
01833 
01834   DIOPROTOCOL_CONNECTION* connection =  (DIOPROTOCOL_CONNECTION*)GetConnection();
01835 
01836   if(connection) connection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_ANSWER);
01837 
01838   if(cmd->GetReceivedFunc())
01839     {
01840       int ssize = cmd->GetReceivedFunc()(this, cmd, xbuffer, ID, param);
01841 
01842       if(ssize != DIOPROTOCOL_ERRORRECEIVED)
01843         {
01844           if(cmd->GetSOUTMask()->GetSize())
01845             {
01846               if(ssize)
01847                 {
01848                   if(SendCommand(type|DIOPROTOCOL_CMDTYPE_ANSWER, ID, xbuffer)) status = DIOPROTOCOL_RESULT_OK;
01849                 }
01850 
01851             } else status = DIOPROTOCOL_RESULT_OK;
01852         }
01853     }
01854 
01855   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, type, (status==DIOPROTOCOL_RESULT_OK)?true:false, NULL, param);
01856 
01857   if(connection) connection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_ANSWER);
01858 
01859   return status;
01860 }
01861 
01862 
01876 bool DIOPROTOCOL::SendCommand(XDWORD type, XDWORD& ID, XBUFFER& xbuffer)
01877 {
01878   if(!IsConnected()) return false;
01879 
01880   HASHCRC32 crc32;
01881   XDWORD    crc32head = 0;
01882   XDWORD    crc32body = 0;
01883 
01884   DIOPROTOCOL_COMMAND* cmd = GetCommand(type&~DIOPROTOCOL_CMDTYPE_ANSWER);
01885   if(!cmd) return false;
01886 
01887   if(xmutexwrites) xmutexwrites->Lock();
01888 
01889   XBYTE   head[DIOPROTOCOL_SIZEOFHEAD];
01890   XDWORD* ptr_head  = (XDWORD*)head;
01891 
01892   memset(head, 0, DIOPROTOCOL_SIZEOFHEAD);
01893 
01894   if(xbuffer.GetSize())
01895     {
01896       crc32.ResetResult();
01897       crc32.Do(xbuffer);
01898       crc32body = crc32.GetResultCRC32();
01899     }
01900 
01901   if(!ID)
01902     {
01903       ID = IDmsgcounter;
01904       ID |= maskID;
01905 
01906       IDmsgcounter++;
01907       if(IDmsgcounter > DIOPROTOCOL_CMDTYPE_SECUENCIALLIMIT)  IDmsgcounter = 0;
01908     }
01909 
01910   ptr_head[0] = DIOPROTOCOL_MSGIDHEAD;
01911   ptr_head[1] = type;
01912   ptr_head[2] = xbuffer.GetSize();
01913   ptr_head[3] = ID;
01914   ptr_head[4] = crc32body;
01915 
01916   crc32.ResetResult();
01917   crc32.Do(head, DIOPROTOCOL_SIZEOFHEAD-sizeof(XDWORD));
01918   crc32head = crc32.GetResultCRC32();
01919 
01920   ptr_head[5] = crc32head;
01921 
01922   bool status = false;
01923 
01924   if(xbuffer.GetSize())
01925     {
01926       XBUFFER xbuffersend;
01927 
01928       xbuffersend.Add(head, DIOPROTOCOL_SIZEOFHEAD);
01929       xbuffersend.Add(xbuffer);
01930 
01931       status = (diostream->Write(xbuffersend) == xbuffersend.GetSize())?true:false;
01932       if(status) diostream->WaitToFlushOutXBuffer(5);
01933     }
01934    else
01935     {
01936       status = (diostream->Write(head, DIOPROTOCOL_SIZEOFHEAD) == DIOPROTOCOL_SIZEOFHEAD)?true:false;
01937       if(status) status = diostream->WaitToFlushOutXBuffer(5);
01938     }
01939 
01940   if(xmutexwrites) xmutexwrites->UnLock();
01941 
01942   return status;
01943 }
01944 
01945 
01959 bool DIOPROTOCOL::Command(XDWORD type, XBUFFER& xbuffer, int timeout)
01960 {
01961   if(!IsConnected())  return false;
01962   if(!watchdog)       return false;
01963 
01964   if(xmutexcommands) xmutexcommands->Lock();
01965 
01966   XDWORD ID     = 0;
01967   bool   status = false;
01968 
01969 
01970   DIOPROTOCOL_COMMAND* cmd = GetCommand(type);
01971   if(cmd)
01972     {
01973       /*
01974      XTRACE_PRINTCOLOR(1, __L("DIOPROTOCOL %s Command  : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
01975                                                                                        , this
01976                                                                                        , ID
01977                                                                                        , type
01978                                                                                        , cmd->GetDescription()
01979                                                                                        , xbuffer.GetSize());
01980       */
01981       if(SendCommand(type, ID, xbuffer))
01982         {
01983           /*
01984          XTRACE_PRINTCOLOR(1, __L("DIOPROTOCOL %s Send     : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
01985                                                                                            , this
01986                                                                                            , ID
01987                                                                                            , type
01988                                                                                            , cmd->GetDescription()
01989                                                                                            , xbuffer.GetSize());
01990           */
01991           xbuffer.Delete();
01992 
01993           //--------------------------------------------
01994           // No Answer
01995           if(!cmd->GetSOUTMask()->IsEmpty())
01996             {
01997               watchdog->Reset();
01998 
01999               while(1)
02000                 {
02001                   if(!IsConnected()) break;
02002 
02003                   DIOPROTOCOL_ANSWER* answer = GetAnswer(type, ID);
02004                   if(answer)
02005                     {
02006                       xbuffer.Delete();
02007                       xbuffer.Add(answer->GetBuffer());
02008 
02009                       DeleteAnswer(type, ID);
02010 
02011                       status = true;
02012                       break;
02013                     }
02014 
02015                   if((int)watchdog->GetMeasureSeconds() >= timeout)
02016                     {
02017                       break;
02018                     }
02019 
02020                   GEN_XSLEEP.MilliSeconds(10);
02021                 }
02022 
02023             } else status = true;
02024 
02025           if(!status)
02026             {
02027              XTRACE_PRINTCOLOR((status?1:4), __L("DIOPROTOCOL %s  : %08X %08X %02d %-35s (%8d)  %s") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
02028                                                                                                               , this
02029                                                                                                               , ID
02030                                                                                                               , type
02031                                                                                                               , cmd->GetDescription()
02032                                                                                                               , xbuffer.GetSize()
02033                                                                                                               , status?__L("Ok"):__L("ERROR!"));
02034             }
02035         }
02036 
02037     }
02038 
02039   if(xmutexcommands) xmutexcommands->UnLock();
02040 
02041   return status;
02042 }
02043 
