02241 bool DIOPROTOCOL::SetFileProgress(DIOPROTOCOL_DIRECTION direction, XPATH& sourcename, XPATH& targetname, XDWORD CRC32)
02242 {
02243   if(sourcename.IsEmpty())  return false;
02244 
02245   fileprogress->Reset();
02246 
02247   fileprogress->SetSourceName(sourcename);
02248 
02249   if(targetname.IsEmpty())
02250          fileprogress->SetTargetName(sourcename);
02251     else fileprogress->SetTargetName(targetname);
02252 
02253   fileprogress->SetActive(false);
02254   fileprogress->SetDirection(direction);
02255   fileprogress->SetPosition(0);
02256 
02257   if(CRC32)
02258         fileprogress->SetCRC32(CRC32);
02259    else fileprogress->SetCRC32(CRC32OfFile(sourcename));
02260 
02261   return true;
02262 }
