02324 
02338 bool DIOPROTOCOL::CMD_GetProtocolVersion(XWORD& version, XWORD& subversion, XWORD& subversionerr)
02339 {
02340   bool result = false;
02341 
02342   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_GETPROTOCOLVERSION);
02343   if(!cmd) return false;
02344 
02345   XBUFFER xbuffer;
02346 
02347   if(Command(cmd->GetType(), xbuffer))
02348     {
02349       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &version, &subversion, &subversionerr);
02350       result = true;
02351     }
02352 
02353   XDWORD param = (version<<8)|subversion;
02354 
02355   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, cmd->GetType(), result, NULL, param);
02356 
02357   return result;
02358 }
