02495 
02508 bool DIOPROTOCOL::CMD_Ping(XDWORD& milliseconds, int timeout)
02509 {
02510   if(!xtimer) return false;
02511 
02512   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_PING);
02513   if(!cmd) return false;
02514 
02515   XBUFFER xbuffer;
02516   XBYTE result = 0;
02517 
02518   xtimer->Reset();
02519 
02520   if(Command(cmd->GetType(), xbuffer, timeout))
02521     {
02522       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result);
02523     }
02524 
02525   milliseconds = (XDWORD)xtimer->GetMeasureMilliSeconds();
02526 
02527   SendEventCommand(this, DIOPROTOCOL_DIRECTION_SEND, cmd->GetType(),result?true:false,NULL,milliseconds);
02528 
02529   return result?true:false;
02530 }
