02752 
02768 bool DIOPROTOCOL::CMD_File(DIOPROTOCOL_DIRECTION direction, XCHAR* sourcename, XCHAR* targetname, int timeout, void* param)
02769 {
02770   if(!xtimer)    return false;
02771 
02772   if(!sourcename) return 0;
02773   if(!targetname) return 0;
02774 
02775   XPATH _sourcename;
02776   XPATH _targetname;
02777 
02778   _sourcename = sourcename;
02779   _targetname = targetname;
02780 
02781   if(_sourcename.IsEmpty()) return false;
02782   if(_targetname.IsEmpty()) return false;
02783 
02784   if(!SetFileProgress(direction,_sourcename,_targetname,0)) return false;
02785 
02786   if(!CMD_InitFileBlock(fileprogress)) return false;
02787 
02788   bool status  = false;
02789 
02790   SendEventFileBlock(DIOPROTOCOL_FILESTATUS_REQUEST , fileprogress->IsActive()
02791                                                     , fileprogress->GetSourceName()
02792                                                     , fileprogress->GetTargetName()
02793                                                     , fileprogress->GetPercent()
02794                                                     , param);
02795   xtimer->Reset();
02796 
02797   while(1)
02798     {
02799       if(fileprogress->IsActive())
02800         {
02801           switch(fileprogress->GetDirection())
02802             {
02803               case DIOPROTOCOL_DIRECTION_UNKNOWN  : fileprogress->SetActive(false);     break;
02804               case DIOPROTOCOL_DIRECTION_GET      : CMD_GetFileBlock(fileprogress);     break;
02805               case DIOPROTOCOL_DIRECTION_SEND     : CMD_SendFileBlock(fileprogress);    break;
02806             }
02807         }
02808 
02809       SendEventFileBlock(DIOPROTOCOL_FILESTATUS_WAIT , fileprogress->IsActive()
02810                                                      , fileprogress->GetSourceName()
02811                                                      , fileprogress->GetTargetName()
02812                                                      , fileprogress->GetPercent()
02813                                                      , param);
02814 
02815       GEN_XSLEEP.MilliSeconds(10);
02816 
02817       if(!IsConnected())                              break;
02818       if((int)xtimer->GetMeasureSeconds()>=timeout)   break;
02819       if(!fileprogress->IsActive())                   break;
02820     }
02821 
02822   if(fileprogress->GetPercent()>=100)  status = true;
02823 
02824   DIOPROTOCOL_FILESTATUS fstatus = DIOPROTOCOL_FILESTATUS_END;
02825   if(!status) fstatus = DIOPROTOCOL_FILESTATUS_ERROR;
02826 
02827   SendEventFileBlock(fstatus , fileprogress->IsActive()
02828                              , fileprogress->GetSourceName()
02829                              , fileprogress->GetTargetName()
02830                              , fileprogress->GetPercent()
02831                              , param);
02832   return status;
02833 }
02834 
