03046 
03063 bool DIOPROTOCOL::CMD_GetDirElement(XCHAR* pathsearch, XCHAR* mask, XPATH& namefile, XBYTE& type, int& sizefile, XDWORD& CRC32)
03064 {
03065   if(!pathsearch) return false;
03066   if(!mask)       return false;
03067 
03068   XBUFFER   xbuffer;
03069   XPATH     _pathsearch;
03070   XSTRING   _mask;
03071   bool      result      = false;
03072 
03073   _pathsearch = pathsearch;
03074   _mask       = mask;
03075 
03076   DIOPROTOCOL_COMMAND* cmd = GetCommand((!_pathsearch.IsEmpty())?DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT:DIOPROTOCOL_CMDTYPE_GETNEXTDIRELEMENT);
03077   if(!cmd) return false;
03078 
03079   if(_mask.IsEmpty()) _mask = __L("*");
03080 
03081   xbuffer.AddWithMask(cmd->GetSINMask(), &_pathsearch, &_mask);
03082 
03083   if(Command(cmd->GetType(), xbuffer))
03084     {
03085       namefile.Empty();
03086 
03087       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &namefile, &type, &sizefile, &CRC32);
03088 
03089       if(!namefile.IsEmpty()) result = true;
03090     }
03091 
03092   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, cmd->GetType(), result?true:false, NULL, 0);
03093 
03094   return result?true:false;
03095 }
03096 
03097 
03113 int DIOPROTOCOL::RCV_GetDirElement(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03114 {
03115   XPATH     pathsearch;
03116   XSTRING   masksearch;
03117   XPATH     pathfile;
03118   XBYTE     typefile     = 0;
03119   int       sizefile     = 0;
03120   XDWORD    crc32value  = 0;
03121   bool      found       = false;
03122 
03123   pathfile.Empty();
03124 
03125   if(cmd->GetType()==DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT)
03126     {
03127       pathsearch.Empty();
03128 
03129       xbuffer.GetWithMask(cmd->GetSINMask(), 0, &pathsearch, &masksearch);
03130 
03131       protocol->GetPathSearch()->Set(pathsearch);
03132       protocol->GetSearchElement()->DeleteFindFileData();
03133 
03134       found = protocol->GetXDir()->FirstSearch(pathsearch, masksearch, protocol->GetSearchElement());
03135     }
03136    else found =  protocol->GetXDir()->NextSearch(protocol->GetSearchElement());
03137 
03138   if(found)
03139     {
03140       typefile    = (XBYTE)protocol->GetSearchElement()->GetType();
03141 
03142       if(protocol->GetSearchElement()->GetType() == XDIRELEMENTTYPE_FILE)
03143         {
03144           pathfile  = protocol->GetPathSearch()->Get();
03145           pathfile += protocol->GetSearchElement()->GetNameFile()->Get();
03146 
03147           HASHCRC32 crc32;
03148 
03149           if(crc32.Do(pathfile, sizefile)) crc32value = crc32.GetResultCRC32();
03150         }
03151 
03152       pathfile  = protocol->GetSearchElement()->GetNameFile()->Get();
03153     }
03154 
03155   xbuffer.Delete();
03156   return xbuffer.AddWithMask(cmd->GetSOUTMask(), &pathfile, typefile, sizefile, crc32value);
03157 }
03158 
