03169 bool DIOPROTOCOL::DeleteAllCommands()
03170 {
03171   if(commands.IsEmpty())  return false;
03172 
03173   commands.DeleteContents();
03174   commands.DeleteAll();
03175 
03176   return true;
03177 }
03178 
03179 
03191 XDWORD DIOPROTOCOL::CRC32OfFile(XPATH& xpath)
03192 {
03193   XDWORD value = 0;
03194 
03195   HASHCRC32* crc32 = new HASHCRC32();
03196   if(!crc32) return false;
03197 
03198   XFILE* GEN_XFACTORY_CREATE(file, Create_File());
03199   if(file)
03200     {
03201       if(file->Open(xpath))
03202         {
03203           crc32->ResetResult();
03204           crc32->Do(file, file->GetSize(), 0);
03205           value  = crc32->GetResultCRC32();
03206 
03207           file->Close();
03208         }
03209 
03210       GEN_XFACTORY.Delete_File(file);
03211     }
03212 
03213   delete crc32;
03214 
03215   return value;
03216 }
03217 
03218 
03230 bool DIOPROTOCOL::CMD_InitFileBlock(DIOPROTOCOL_FILE* fp)
03231 {
03232   if(!fp) return false;
03233 
03234   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_INITFILEBLOCK);
03235   if(!cmd) return false;
03236 
03237   XBUFFER xbuffer;
03238   bool    result = false;
03239 
03240   xbuffer.AddWithMask(cmd->GetSINMask(), fp->GetSourceName()
03241                                        , fp->GetTargetName()
03242                                        , fp->GetDirection()
03243                                        , fp->GetCRC32());
03244 
03245 
03246   if(Command(cmd->GetType(), xbuffer))
03247     {
03248       if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result))
03249         {
03250           if(result) GetFileProgress()->SetActive(true);
03251         }
03252     }
03253 
03254   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), result, NULL, 0);
03255 
03256   return result;
03257 }
03258 
03259 
03275 int DIOPROTOCOL::RCV_InitFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03276 {
03277   XPATH                  sourcename;
03278   XPATH                  targetname;
03279   XBYTE                  _direction   = 0;
03280   DIOPROTOCOL_DIRECTION  direction    = (DIOPROTOCOL_DIRECTION)_direction;
03281   XDWORD                 CRC32        = 0;
03282   bool                   result       = false;
03283 
03284   xbuffer.GetWithMask(cmd->GetSINMask(), 0, &sourcename, &targetname, &_direction, &CRC32);
03285 
03286   direction = (DIOPROTOCOL_DIRECTION)_direction;
03287 
03288   if(protocol->SetFileProgress(direction, sourcename, targetname, CRC32))
03289     {
03290       protocol->GetFileProgress()->SetActive(true);
03291       result = true;
03292     }
03293 
03294   xbuffer.Delete();
03295   return xbuffer.AddWithMask(cmd->GetSOUTMask(), result);
03296 }
03297 
03298 
03310 bool DIOPROTOCOL::CMD_GetFileBlock(DIOPROTOCOL_FILE* fp)
03311 {
03312   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_GETFILEBLOCK);
03313   if(!cmd) return false;
03314 
03315   if(!fp) return false;
03316 
03317   if(!fp->IsActive()) return false;
03318 
03319   XBUFFER xbuffer;
03320   bool    status = false;
03321 
03322   if(Command(cmd->GetType(), xbuffer))
03323     {
03324       XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03325       if(filebuffer)
03326         {
03327           XBYTE   islastblock   = 0;
03328           XDWORD  size          = 0;
03329           XDWORD  crc32         = 0;
03330           XDWORD  sizeblock     = DIOPROTOCOL_MAXFILEBLOCK;
03331           XBYTE   percent       = 0;
03332 
03333           memset(filebuffer, 0, DIOPROTOCOL_MAXFILEBLOCK);
03334 
03335           if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0  , &islastblock
03336                                                         , &size
03337                                                         , &crc32
03338                                                         , &sizeblock
03339                                                         , &percent
03340                                                         , filebuffer
03341                                                         , &sizeblock))
03342 
03343             {
03344               fp->SetSize(size);
03345               fp->SetCRC32(crc32);
03346               fp->SetSizeBlock(sizeblock);
03347 
03348               if(fp->GetSizeBlock()) status = fp->WriteBlock(filebuffer);
03349 
03350               if(islastblock)
03351                 {
03352                   XDWORD valuecrc = CRC32OfFile((*fp->GetTargetName()));
03353                   if(crc32!=valuecrc) fp->Erase();
03354 
03355                   fp->SetActive(false);
03356                 }
03357             }
03358 
03359           delete[] filebuffer;
03360         }
03361     }
03362 
03363   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), status, NULL, 0);
03364 
03365   return status;
03366 }
03367 
03368 
03384 int DIOPROTOCOL::RCV_GetFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03385 {
03386   DIOPROTOCOL_FILE*   fp;
03387   int                 ssize  = 0;
03388   bool                status = false;
03389 
03390   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03391   if(filebuffer)
03392     {
03393       memset(filebuffer, 0, DIOPROTOCOL_MAXFILEBLOCK);
03394 
03395       fp = protocol->GetFileProgress();
03396       if(fp)
03397         {
03398           XBYTE islastblock = 0;
03399 
03400           fp->SetSizeBlock(DIOPROTOCOL_MAXFILEBLOCK);
03401 
03402           fp->ReadBlock(filebuffer);
03403 
03404           //if(fp->GetSizeBlock()!=DIOPROTOCOL_MAXFILEBLOCK)  islastblock = 1;
03405           if(fp->GetSize() == fp->GetPosition()) islastblock = 1;
03406 
03407           if(fp->GetSizeBlock())
03408             {
03409               xbuffer.Delete();
03410               ssize = xbuffer.AddWithMask(cmd->GetSOUTMask(), islastblock
03411                                                             , fp->GetSize()
03412                                                             , fp->GetCRC32()
03413                                                             , fp->GetSizeBlock()
03414                                                             , fp->GetPercent()
03415                                                             , filebuffer
03416                                                             , fp->GetSizeBlock());
03417 
03418 
03419 
03420               param  = 1;
03421               status = true;
03422             }
03423 
03424           if(islastblock) fp->SetActive(false);
03425         }
03426 
03427       delete[] filebuffer;
03428     }
03429 
03430   if(!status) ssize = 0;
03431 
03432   return ssize;
03433 }
03434 
03435 
03447 bool DIOPROTOCOL::CMD_SendFileBlock(DIOPROTOCOL_FILE* fp)
03448 {
03449   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_SENDFILEBLOCK);
03450   if(!cmd) return false;
03451 
03452   if(!fp) return false;
03453 
03454   if(!fp->IsActive()) return false;
03455 
03456   XBUFFER xbuffer;
03457   bool    status = false;
03458 
03459 
03460   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03461   if(filebuffer)
03462     {
03463       XBYTE islastblock = 0;
03464 
03465       fp->SetSizeBlock(DIOPROTOCOL_MAXFILEBLOCK);
03466 
03467       fp->ReadBlock(filebuffer);
03468 
03469       //if(fp->GetSizeBlock()!=DIOPROTOCOL_MAXFILEBLOCK)  islastblock = 1;
03470       if(fp->GetSize() == fp->GetPosition()) islastblock = 1;
03471 
03472       if(fp->GetSizeBlock())
03473         {
03474           xbuffer.Delete();
03475           XDWORD size = xbuffer.AddWithMask(cmd->GetSINMask(), islastblock
03476                                                              , fp->GetSize()
03477                                                              , fp->GetCRC32()
03478                                                              , fp->GetSizeBlock()
03479                                                              , fp->GetPercent()
03480                                                              , filebuffer
03481                                                              , fp->GetSizeBlock());
03482 
03483 
03484           if(Command(cmd->GetType(), xbuffer))
03485             {
03486               XBYTE result = 0;
03487 
03488               if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result))
03489                 {
03490                   if(result) status = true;
03491                 }
03492 
03493               if(islastblock) fp->SetActive(false);
03494             }
03495         }
03496 
03497       delete[] filebuffer;
03498     }
03499 
03500   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), status, NULL, 0);
03501 
03502   return status;
03503 }
03504 
03505 
03521 int DIOPROTOCOL::RCV_SendFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03522 {
03523   XDWORD  filesize    = 0;
03524   XDWORD  crc32       = 0;
03525   XDWORD  sizeblock   = DIOPROTOCOL_MAXFILEBLOCK;
03526   XBYTE percent     = 0;
03527   int   ssize       = 0;
03528   bool  status      = false;
03529 
03530   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03531   if(filebuffer)
03532     {
03533       XBYTE islastblock = 0;
03534 
03535       memset(filebuffer,0,DIOPROTOCOL_MAXFILEBLOCK);
03536 
03537       xbuffer.GetWithMask(cmd->GetSINMask(), 0, &islastblock
03538                                               , &filesize
03539                                               , &crc32
03540                                               , &sizeblock
03541                                               , &percent
03542                                               , filebuffer
03543                                               , &sizeblock);
03544 
03545       DIOPROTOCOL_FILE* fp = protocol->GetFileProgress();
03546       if(fp)
03547         {
03548           fp->SetSize(filesize);
03549           fp->SetCRC32(crc32);
03550           fp->SetSizeBlock(sizeblock);
03551 
03552           fp->WriteBlock(filebuffer);
03553 
03554           if(fp->GetSizeBlock())
03555             {
03556               xbuffer.Delete();
03557               ssize = xbuffer.AddWithMask(cmd->GetSOUTMask(), fp->GetSizeBlock()?1:0);
03558 
03559               param  = 1;
03560               status = true;
03561             }
03562 
03563           if(islastblock)
03564             {
03565               XDWORD valuecrc = protocol->CRC32OfFile((*fp->GetTargetName()));
03566               if(crc32!=valuecrc) fp->Erase();
03567 
03568               fp->SetActive(false);
03569             }
03570         }
03571 
03572       delete[] filebuffer;
03573     }
03574 
03575   if(!status) ssize = 0;
03576 
03577   return ssize;
03578 }
03579 
03580 
03581 #ifdef DIO_ALERTS_ACTIVE
03582 
03594 bool DIOPROTOCOL::CMD_SendAlert(DIOALERT* alert)
03595 {
03596   if(!alert) return false;
03597 
03598   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_SENDALERT);
03599   if(!cmd) return false;
03600 
03601   XBUFFER  xbuffer;
03602   XSTRING  datetimestr;
03603   XDWORD   applicationversion;
03604   XDWORD   applicationsubversion;
03605   XDWORD   applicationsubversionerr;
03606   bool     result     = false;
03607 
03608   alert->GetDateTime()->GetDateTimeToString(XDATETIME_FORMAT_STANDARD, datetimestr);
03609   alert->GetApplicationVersion(applicationversion, applicationsubversion, applicationsubversionerr);
03610 
03611   xbuffer.AddWithMask(cmd->GetSINMask() , &datetimestr
03612                                         , alert->GetApplicationID()
03613                                         , applicationversion
03614                                         , applicationsubversion
03615                                         , applicationsubversionerr
03616                                         , alert->GetID()                                        
03617                                         , (XBYTE)alert->GetLevel()
03618                                         , alert->GetOrigin()
03619                                         , alert->GetTitle()
03620                                         , alert->Get_Message());
03621 
03622   if(Command(cmd->GetType(), xbuffer))
03623     {
03624       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result);
03625     }
03626 
03627   SendEventCommand(this, DIOPROTOCOL_DIRECTION_SEND, cmd->GetType(), result, NULL, 0);
03628 
03629   return result;
03630 }
03631 
03632 
03648 int DIOPROTOCOL::RCV_SendAlert(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03649 {
03650   XSTRING  datetimestr;
03651   XSTRING  applicationID;
03652   XDWORD   applicationversion       = 0;
03653   XDWORD   applicationsubversion    = 0;
03654   XDWORD   applicationsubversionerr = 0;
03655   XDWORD   ID                       = 0;
03656   XDWORD   type                     = 0;
03657   XBYTE    level                    = 0;
03658   XSTRING  origin;
03659   XSTRING  title;
03660   XSTRING  message;
03661   bool     result                   = false;
03662 
03663   datetimestr.AdjustSize(_MAXSTR);
03664   applicationID.AdjustSize(_MAXSTR);
03665   origin.AdjustSize(_MAXSTR);
03666   title.AdjustSize(_MAXSTR);
03667   message.AdjustSize(2048);
03668 
03669   xbuffer.GetWithMask(cmd->GetSINMask(), 0, &datetimestr
03670                                           , &applicationID
03671                                           , &applicationversion
03672                                           , &applicationsubversion
03673                                           , &applicationsubversionerr
03674                                           , &ID
03675                                           , &type
03676                                           , &level
03677                                           , &origin
03678                                           , &title
03679                                           , &message);
03680 
03681   datetimestr.AdjustSize();
03682   applicationID.AdjustSize();
03683   origin.AdjustSize();
03684   title.AdjustSize();
03685   message.AdjustSize();
03686 
03687   if((!title.IsEmpty())  && (!origin.IsEmpty()) && (!message.IsEmpty()))
03688     {
03689       DIOALERT alert;
03690 
03691       alert.GetDateTime()->GetDateTimeFromString(datetimestr, XDATETIME_FORMAT_STANDARD);
03692       alert.GetApplicationID()->Set(applicationID);
03693       alert.SetApplicationVersion(applicationversion, applicationsubversion, applicationsubversionerr);
03694       alert.SetID(ID);      
03695       alert.SetLevel((DIOALERTLEVEL)level);
03696       alert.GetOrigin()->Set(origin);
03697       alert.GetTitle()->Set(title);
03698       alert.Get_Message()->Set(message);
03699 
03700       if(protocol->GetApplicationData())
03701         {
03702           DIOPROTOCOL_APPLICATIONDATA* applicationdata = (DIOPROTOCOL_APPLICATIONDATA*)protocol->GetApplicationData();
03703           if(applicationdata)  result = applicationdata->AddAlert(alert);
03704 
03705           if(result)
03706             {
03707               DIOPROTOCOL_XEVENT xevent(protocol, DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
03708 
03709               xevent.SetProtocol(protocol);
03710               xevent.SetAlert(&alert);
03711 
03712               protocol->PostEvent(&xevent);
03713             }
03714         }
03715     }
03716 
03717   xbuffer.Delete();
03718   return xbuffer.AddWithMask(cmd->GetSOUTMask(), result);
03719 }
