03724 
03734 void DIOPROTOCOL::ThreadReceived(void* param)
03735 {
03736   DIOPROTOCOL* protocol = (DIOPROTOCOL*)param;
03737   if(!protocol) return;
03738 
03739   if(protocol->doexit) return;
03740 
03741   if(!protocol->xthreadreceived)              return;
03742   if(!protocol->xthreadreceived->IsRunning()) return;
03743 
03744   if(protocol->GetDIOStream())
03745     {
03746       if(protocol->IsConnected())
03747         {
03748           protocol->Received();
03749         }
03750     }
03751 }
03752 
03753 
03763 void DIOPROTOCOL::ThreadAsks(void* param)
03764 {
03765   DIOPROTOCOL* protocol = (DIOPROTOCOL*)param;
03766   if(!protocol) return;
03767 
03768   if(protocol->doexit) return;
03769 
03770   if(!protocol->xthreadreceived)              return;
03771   if(!protocol->xthreadreceived->IsRunning()) return;
03772 
03773   if(protocol->GetDIOStream())
03774     {
03775       if(protocol->IsConnected())
03776         {
03777           if(!protocol->asks.IsEmpty())
03778             {
03779               DIOPROTOCOL_ANSWER* ask = protocol->GetAsk(0);
03780               if(ask)
03781                 {
03782                   XDWORD t = ask->GetType();
03783                   protocol->ReceivedHandle(ask->GetType(), ask->GetID(), ask->GetBuffer());
03784                   protocol->DeleteAsk(0);
03785                 }
03786             }
03787 
03788           //if(protocol->xtimer) protocol->GEN_XSLEEP.MilliSeconds(50);
03789         }
03790     }
03791 }
