03802 void DIOPROTOCOL::Clean()
03803 {
03804   diostream                     = NULL;
03805 
03806   protocolversion               = DIOPROTOCOL_VERSION;
03807   protocolsubversion            = DIOPROTOCOL_SUBVERSION;
03808   protocolsubversionerr         = DIOPROTOCOL_SUBVERSIONERR;
03809 
03810   applicationversion            = 0;
03811   applicationsubversion         = 0;
03812   applicationsubversionerr      = 0;
03813 
03814   isinitialized                 = false;
03815   isserverinitialized           = false;
03816 
03817   xdatetime                     = NULL;
03818   xtimer                        = NULL;
03819   watchdog                      = NULL;
03820 
03821   xdir                          = NULL;
03822 
03823   connectionsmanager             = NULL;
03824   connection                     = NULL;
03825   applicationdata               = NULL;
03826   isownapplicationdata          = false;
03827 
03828   maskID                        = 0;
03829 
03830   IDmsgcounter                  = 1;
03831   timerfileprogress             = NULL;
03832 
03833   xdir                          = NULL;
03834 
03835   doexit                        = false;
03836 
03837   xmutexanswers                 = NULL;
03838   xmutexasks                    = NULL;
03839   xmutexcommands                = NULL;
03840   xmutexreads                   = NULL;
03841   xmutexwrites                  = NULL;
03842   xmutexfilesprogress           = NULL;
03843 
03844   xthreadreceived               = NULL;
03845   xthreadasks                   = NULL;
03846 }
