01188   while(index < ProtocolConnections_GetNAvailable())
01189     {
01190       DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections.Get(index);
01191       if(protocolconnection)
01192         {
01193           if((!protocolconnection->IsInUse()))
01194             {
01195               DIOPROTOCOL* protocol = protocolconnection->GetDIOProtocol();
01196 
01197               protocolconnections.Delete(protocolconnection);
01198               delete protocolconnection;
01199 
01200               DeleteProtocol(protocol);
01201 
01202             } else index++;
01203 
01204         } else index++;
01205     }
01206 
01207   xmutexprocotolconnections->UnLock();
01208 
01209   return true;
01210 }
