01224 bool DIOPROTOCOL_CONNECTIONSMANAGER::WaitToAnyConnectionIsConnected(int timeout)
01225 {
01226   int  index  = 0;
01227   bool status = true;
01228 
01229   xtimerout->Reset();
01230 
01231   while(1)
01232     {
01233       if(ProtocolConnections_GetNConnected())
01234         {
01235           status = false;
01236           break;
01237         }
01238 
01239       GEN_XSLEEP.MilliSeconds(10);
01240 
01241       if(xtimerout->GetMeasureSeconds() >= (XDWORD)timeout)
01242         {
01243           status = false;
01244           break;
01245         }
01246     }
01247 
01248   return status;
01249 }
