01263 bool DIOPROTOCOL_CONNECTIONSMANAGER::WaitToAllConnectionsCanBeDeleted(int timeout)
01264 {
01265   if(!xmutexprocotolconnections)  return false;
01266 
01267   xmutexprocotolconnections->Lock();
01268 
01269   int  index  = 0;
01270   bool status = true;
01271 
01272   if(timeout) xtimerout->Reset();
01273 
01274   while(1)
01275     {
01276       bool candelete = true;
01277 
01278       for(int index=0; index< ProtocolConnections_GetNAvailable(); index++)
01279         {
01280           DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections.Get(index);
01281           if(protocolconnection)
01282             {
01283               if(protocolconnection->IsInUse()) candelete = false;
01284             }
01285         }
01286 
01287       if(candelete) break;
01288 
01289       GEN_XSLEEP.MilliSeconds(10);
01290 
01291       if(timeout)
01292         {
01293           if(xtimerout->GetMeasureSeconds() > 0)
01294             {
01295               if(xtimerout->GetMeasureSeconds() >= (XDWORD)timeout)
01296                 {
01297                   status = false;
01298                   break;
01299                 }
01300             }
01301         }
01302     }
01303 
01304   xmutexprocotolconnections->UnLock();
01305 
01306   return status;
01307 }
