01322 bool DIOPROTOCOL_CONNECTIONSMANAGER::SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE type, DIOPROTOCOL_CONNECTION* protocolconnection)
01323 {
01324   if(!protocolconnection) return false;
01325 
01326   DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT xevent(this,type);
01327 
