01329   xevent.SetDIOStreamConfig(protocolconnection->GetDIOStreamConfig());
01330   xevent.SetDIOProtocol(protocolconnection->GetDIOProtocol());
01331   xevent.SetProtocolConnection(protocolconnection);
01332   xevent.SetProtocolConnectionsManager(this);
01333 
01334   return PostEvent(&xevent);
