01398 void DIOPROTOCOL_CONNECTIONSMANAGER::ManageProtocolConnectionsServer()
01399 {
01400   if(!xthreadconnections)              return;
01401   if(!xthreadconnections->IsRunning()) return;
01402 
01403   xtimerconnections->Reset();
01404 
01405   ProtocolConnections_DeleteDisconnected();
01406 
01407   if(protocolconnectionsnlimit != DIOPROTOCOL_CONNECTIONS_UNLIMITEDNCONNECTIONS)
01408     {
01409       if(!protocolconnectionsnlimit) return;
01410 
01411       if(ProtocolConnections_GetNConnected() >= protocolconnectionsnlimit)
01412         {
01413           return;
01414         }
01415     }
01416 
01417   ProtocolConnections_SendEventConnected();
01418 
01419   if(!ProtocolConnections_GetNFreeToConnect())
01420     {
01421       DIOPROTOCOL_CONNECTION* protocolconnection = new DIOPROTOCOL_CONNECTION(diostreamcfg);
01422       if(protocolconnection)
01423         {
01424           DIOPROTOCOL* protocol = CreateProtocol();
01425           if(protocol)
01426             {
01427               protocolconnection->SetDIOProtocol(protocol);
01428 
01429               if(protocolconnection->Connect())
01430                 {
01431                   if(xmutexprocotolconnections) xmutexprocotolconnections->Lock();
01432 
01433                   protocol->SetConnection(protocolconnection);
01434 
01435                   protocolconnections.Add(protocolconnection);
01436 
01437                   if(xmutexprocotolconnections) xmutexprocotolconnections->UnLock();
01438                 }
01439             }
01440            else
01441             {
01442               DeleteProtocol(protocol);
01443               protocol = NULL;
01444 
01445               delete protocolconnection;
01446               protocolconnection = NULL;
01447             }
01448         }
01449        else
01450         {
01451           delete protocolconnection;
01452           protocolconnection = NULL;
01453         }
01454     }
01455 }
01456 
01457 
01465 void DIOPROTOCOL_CONNECTIONSMANAGER::ManageProtocolConnectionsClient()
01466 {
01467   if(!xmutexprocotolconnections)       return;
01468   if(!xthreadconnections)              return;
01469   if(!xthreadconnections->IsRunning()) return;
01470   if(!xtimerconnections)               return;
01471   if(!xtimerclienttry)                return;
01472 
01473   ProtocolConnections_DeleteDisconnected();
01474 
01475   if(protocolconnectionsnlimit != DIOPROTOCOL_CONNECTIONS_UNLIMITEDNCONNECTIONS)
01476     {
01477       if(!protocolconnectionsnlimit) return;
01478 
01479       if(ProtocolConnections_GetNConnected() >= protocolconnectionsnlimit)
01480         {
01481           return;
01482         }
01483     }
01484 
01485   if(xtimerclienttry->GetMeasureSeconds() <= DIOPROTOCOL_CONNECTIONS_DEFAULTIMETRYCONNECTIONS) return;
01486 
01487   //DEBUG_PRINT(__L("---------------------------------------------------------------"));
01488 
01489   for(int c=0;c<(int)targetURLs.GetSize();c++)
01490     {
01491       if(!xthreadconnections->IsRunning()) break;
01492 
01493       DIOURL* URLclient = (DIOURL*)targetURLs.Get(c);
01494       if(URLclient)
01495         {
01496           bool haveservervalid = false;
01497 
01498           switch(diostreamcfg->GetType())
01499             {
01500               case DIOSTREAMTYPE_UNKNOWN    : break;
01501               case DIOSTREAMTYPE_UART       : break;
01502               case DIOSTREAMTYPE_USB        : break;
01503               case DIOSTREAMTYPE_BLUETOOTH  : break;
01504 
01505               case DIOSTREAMTYPE_TCPIP      : { DIOSTREAMTCPIPCONFIG* scfg = (DIOSTREAMTCPIPCONFIG*)diostreamcfg;
01506                                                 XSTRING               address;
01507                                                 if(scfg)
01508                                                   {
01509                                                     if(URLclient->IsEmpty() && diostreamenumservers)
01510                                                       {
01511                                                         diostreamenumservers->Search();
01512 
01513                                                         while(diostreamenumservers->IsSearching())
01514                                                           {
01515                                                             GEN_XSLEEP.MilliSeconds(1);
01516                                                           }
01517 
01518                                                         diostreamenumservers->StopSearch(true);
01519 
01520                                                         if(diostreamenumservers->AreDevicesAvailable())
01521                                                           {
01522                                                             DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)diostreamenumservers->GetDevices()->Get(0);
01523                                                             if(device) device->GetIP()->GetXString(address);
01524                                                           }
01525 
01526                                                       } else address = URLclient->Get();
01527 
01528                                                     if(!address.IsEmpty())
01529                                                       {
01530                                                         DIOURL* url= new DIOURL();
01531                                                         if(url)
01532                                                           {
01533                                                             (*url) = address.Get();
01534 
01535                                                             url->ResolveURL();
01536                                                             scfg->GetRemoteURL()->Set(address);
01537                                                             delete url;
01538 
01539                                                             haveservervalid = true;
01540                                                           }
01541                                                       }
01542                                                   }
01543 
01544                                                 break;
01545                                               }
01546 
01547             }
01548 
01549           if(haveservervalid)
01550             {
01551               DIOPROTOCOL_CONNECTION* protocolconnection = new DIOPROTOCOL_CONNECTION(diostreamcfg);
01552               if(protocolconnection)
01553                 {
01554                   DIOPROTOCOL* protocol = CreateProtocol();
01555                   if(protocol)
01556                     {
01557                       protocolconnection->SetDIOProtocol(protocol);
01558 
01559                       if(protocolconnection->Connect())
01560                         {
01561                           xtimerconnections->Reset();
01562 
01563                           DIOSTREAMTCPIPCONFIG* scfg = (DIOSTREAMTCPIPCONFIG*)diostreamcfg;
01564 
01565                           while(1)
01566                             {
01567                               if(protocolconnection->GetDIOProtocol()->IsConnected())
01568                                 {
01569                                   if(xmutexprocotolconnections) xmutexprocotolconnections->Lock();
01570 
01571                                   protocol->SetConnection(protocolconnection);
01572                                   protocolconnections.Add(protocolconnection);
01573 
01574                                   if(xmutexprocotolconnections) xmutexprocotolconnections->UnLock();
01575 
01576                                   SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_CONNECTEDCONNECTION, protocolconnection);
01577 
01578                                   break;
01579                                 }
01580 
01581                               if((xtimerconnections->GetMeasureSeconds()>=5) || (!xthreadconnections->IsRunning()))
01582                                 {
01583                                   DeleteProtocol(protocol);
01584                                   protocol = NULL;
01585 
01586                                   delete protocolconnection;
01587                                   protocolconnection = NULL;
01588 
01589                                   break;
01590                                 }
01591 
01592                               GEN_XSLEEP.MilliSeconds(50);
01593                             }
01594 
01595                          XTRACE_PRINTCOLOR(1, __L("Trying to connect to %s [%s]: %s (%d) seconds."),  URLclient->Get(), scfg->GetRemoteURL()->Get(), protocolconnection?__L("Connected!."):__L("Not Connected!."), xtimerconnections->GetMeasureSeconds());
01596                         }
01597                        else
01598                         {
01599                           DeleteProtocol(protocol);
01600                           protocol = NULL;
01601 
01602                           delete protocolconnection;
01603                           protocolconnection = NULL;
01604                         }
01605                     }
01606                    else
01607                     {
01608                       delete protocolconnection;
01609                       protocolconnection = NULL;
01610                     }
01611                 }
01612             }
01613         }
01614     }
01615 
01616   xtimerclienttry->Reset();
01617 }
