01619 
01629 void DIOPROTOCOL_CONNECTIONSMANAGER::ThreadProtocolConnections(void* param)
01630 {
01631   DIOPROTOCOL_CONNECTIONSMANAGER* protocolconnections = (DIOPROTOCOL_CONNECTIONSMANAGER*)param;
01632   if(!protocolconnections) return;
01633 
01634   if(protocolconnections->isserver)
01635     {
01636       protocolconnections->ManageProtocolConnectionsServer();
01637     }
01638    else
01639     {
01640       protocolconnections->ManageProtocolConnectionsClient();
01641     }
01642 
01643   int c=0;
01644   while(c < protocolconnections->ProtocolConnections_GetNAvailable())
01645     {
01646       DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections->protocolconnections.Get(c);
01647       if(protocolconnection)
01648         {
01649           DIOPROTOCOL* protocol = protocolconnection->GetDIOProtocol();
01650           if(protocol)
01651             {
01652               if(protocol->IsConnected())
01653                 {
01654                   if(!protocol->IsInitialized())
01655                     {
01656                       if(protocolconnections->isserver)
01657                         {
01658                           protocolconnection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01659 
01660                           protocol->GetDIOStream()->GetXTimerNotActivity()->Reset();
01661                           protocolconnections->SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_INITPROTOCOL, protocolconnection);
01662                           if(protocol->IsInitialized()) protocol->CMD_SendIsInitServerProtocol(true);
01663 
01664                           protocolconnection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01665                         }
01666                        else
01667                         {
01668                           XTIMER* GEN_XFACTORY_CREATE(timerout, CreateTimer())
01669                           
01670                           while(1)
01671                             {
01672                               if(protocolconnection->GetDIOProtocol()->IsDisconnected()) break;
01673                               if(protocol->IsServerInitialized())                       break;
01674 
01675                               if(timerout)
01676                                 {
01677                                   if(timerout->GetMeasureSeconds() > 5) break;
01678                                 }
01679 
01680                               GEN_XSLEEP.MilliSeconds(10);
01681                             }
01682 
01683                           GEN_XFACTORY.DeleteTimer(timerout);
01684 
01685 
01686                           if(protocol->IsServerInitialized())
01687                             {
01688                               protocolconnection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01689 
01690                               protocol->GetDIOStream()->GetXTimerNotActivity()->Reset();
01691                               protocolconnections->SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_INITPROTOCOL, protocolconnection);
01692 
01693                               protocolconnection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01694                             }
01695                         }
01696 
01697                      XTRACE_PRINTCOLOR((protocol->IsInitialized()?2:4), __L("Protocol Connection Manager: %s Init Protocol %s") , protocolconnections->isserver?__L("Server"):__L("Client"), protocol->IsInitialized()?__L("Ok!"):__L("ERROR!"));
01698 
01699                       if(!protocol->IsInitialized())
01700                         {
01701                           protocolconnections->ProtocolConnections_Disconnect(c);
01702                         }
01703                     }
01704                    else
01705                     {
01706                       if(protocol->GetDIOStream()->GetXTimerNotActivity()->GetMeasureSeconds() > DIOPROTOCOL_CONNECTIONS_DEFAULTIMECHECKCONNECTIONS)
01707                         {
01708                           XDWORD milliseconds;
01709 
01710                           bool status = protocol->CMD_Ping(milliseconds);
01711                           if(!status)
01712                             {
01713                               protocolconnections->ProtocolConnections_Disconnect(c);
01714 
01715                              XTRACE_PRINTCOLOR(status?1:4, __L("Protocol Connection Manager: Check connection protocol %s"), status?__L("Ok!"):__L("ERROR!"));
01716                             }
01717                         }
01718                     }
01719                 }
01720             }
01721         }
01722 
01723       c++;
01724     }
01725 }
