01436         {
01437           DIOSMTPRECIPIENT* recipient = recipients.Get(c);
01438           if(recipient)
01439             {
01440               string.Format(__L("%s<%s>"), recipient->GetName()->Get(), recipient->GetEmail()->Get());
01441 
01442               switch(recipient->GetType())
01443                 {
01444                   case DIOSMTPRECIPIENTTYPE_UNKNOWN : break;
01445 
01446                   case DIOSMTPRECIPIENTTYPE_TO      : if(!to_line.IsEmpty()) to_line+= __L(",");
01447                                                       to_line += string.Get();
01448                                                       break;
01449 
01450                   case DIOSMTPRECIPIENTTYPE_CC      : if(!cc_line.IsEmpty()) cc_line+= __L(",");
01451                                                       cc_line += string.Get();
01452                                                       break;
01453 
01454                   case DIOSMTPRECIPIENTTYPE_BCC     : if(!bcc_line.IsEmpty()) bcc_line+= __L(",");
01455                                                       bcc_line += string.Get();
01456                                                       break;
01457                 }
01458             }
01459         }
01460     }
01461 
01462   string.Empty();
01463 
01464   /*
01465   XDATETIME* GEN_XFACTORY_CREATE(datetime, CreateTime())
01466   if(datetime)
01467     {
01468       datetime->Read();
01469 
01470       string.Format(__L("Date: %d %s %d %d:%d:%d\r\n"), datetime->GetDay()
01471                                                       , month[datetime->GetMonth()-1]
01472                                                       , datetime->GetYear()
01473                                                       , datetime->GetHours()
01474                                                       , datetime->GetMinutes()
01475                                                       , datetime->GetSeconds());
01476 
01477 
01478       GEN_XFACTORY.DeleteTime(datetime);
01479     }
01480 
01481   if(string.IsEmpty()) return false;
01482   header+= string;
01483   */
01484 
01485   string.Format(__L("From: %s <%s>\r\n"),   GetSenderName()->Get(), GetSenderEmail()->Get());
01486   header+= string;
01487 
01488 
01489   if(!GetReplytoEmail()->IsEmpty())
01490     {
01491       string.Format(__L("Reply-To: %s\r\n"),  GetReplytoEmail()->Get());
01492       header+= string;
01493     }
01494 
01495   XSTRING defaultxmailer;
01496 
01497   defaultxmailer  = GEN_VERSION.GetName()->Get();
01498   defaultxmailer += __L(" ");
01499   defaultxmailer += GEN_VERSION.GetVersion()->Get();
01500 
01501   string.Format(__L("X-Mailer: %s\r\n"),  GetXMailer()->IsEmpty()? defaultxmailer.Get() : GetXMailer()->Get());
01502   header+= string;
01503 
01504   switch(xpriority)
01505     {
01506       case DIOSMTPXPRIORITY_HIGH    : string = __L("X-Priority: 2 (High)\r\n");     break;
01507       case DIOSMTPXPRIORITY_UNKNOWN :
01508       case DIOSMTPXPRIORITY_NORMAL  : string = __L("X-Priority: 3 (Normal)\r\n");   break;
01509       case DIOSMTPXPRIORITY_LOW     : string = __L("X-Priority: 4 (Low)\r\n");      break;
01510     }
01511 
01512   if(string.IsEmpty()) return false;
01513   header+= string;
01514 
01515   if(!to_line.IsEmpty())
01516     {
01517       string.Format(__L("To: %s\r\n"),  to_line.Get());
01518       header+= string;
01519     }
01520 
01521   if(!cc_line.IsEmpty())
01522     {
01523       string.Format(__L("Cc: %s\r\n"),  cc_line.Get());
01524       header+= string;
01525     }
01526 
01527   if(!bcc_line.IsEmpty())
01528     {
01529       string.Format(__L("Bcc: %s\r\n"), bcc_line.Get());
01530       header+= string;
01531     }
01532 
01533 
01534   string.Empty();
01535   switch(contenttype)
01536     {
01537       case DIOSSMPTCONTENTTYPE_PLAINTTEXT : string.Format(__L("Subject: %s\r\n"), GetSubject()->IsEmpty()?__L(" "):GetSubject()->Get());
01538                                             header+= string;
01539                                             break;
01540 
01541       case DIOSSMPTCONTENTTYPE_UTF8       : { XBUFFER subjectUTF8;
01542                                               XSTRING subjectbase64;
01543 
01544                                               GetSubject()->ConvertToUTF8(subjectUTF8);
01545 
01546                                               subjectUTF8.ConvertToBase64(subjectbase64);
01547 
01548                                               string = __L("Subject: =?UTF-8?B?");
01549                                               string += subjectbase64;
01550                                               string += __L("?=\r\n");
01551                                             }
01552                                             break;
01553     }
01554 
01555   header += string;
01556 
01557 
01558   string = __L("MIME-Version: 1.0\r\n");
01559 
01560   if(attachments.GetSize())
01561     {
01562       XSTRING string2;
01563       string2.Format(__L("Content-Type: multipart/mixed; boundary=\"%s\"\r\n\r\n--%s\n\r"), DIOSMTP_BOUNDARYTEXT, DIOSMTP_BOUNDARYTEXT);
01564       string += string2;
01565     }
01566 
01567   switch(contenttype)
01568     {
01569       case DIOSSMPTCONTENTTYPE_PLAINTTEXT : string += __L("Content-type: text/plain; charset=utf-8\r\n"); break;
01570       case DIOSSMPTCONTENTTYPE_UTF8       : string += __L("Content-type: text/plain; charset=utf-8\r\n"); break;
01571     }
01572 
01573 //string += __L("Content-Transfer-Encoding: 7bit\r\n");
01574   string += __L("\r\n");
01575 
01576   header += string;
01577 
01578   return true;
01579 }
