00332 bool DIOSNMP_BASE::Open(XCHAR* IPtarget, int port, bool checkavailableNMS, XCHAR* localIP)
00333 {
00334   if(!IPtarget)     return false;
00335   if(!IPtarget[0])  return false;
00336 
00337   diostreamudpcfg = new DIOSTREAMUDPCONFIG();
00338   if(!diostreamudpcfg)  return false;
00339 
00340   diostreamudpcfg->SetMode(DIOSTREAMMODE_CLIENT);
00341 
00342   if(localIP) diostreamudpcfg->GetLocalIP()->Set(localIP);
00343 
00344   diostreamudpcfg->GetRemoteURL()->Set(IPtarget);
00345   diostreamudpcfg->SetRemotePort(port);
00346 
00347   bool status = false;
00348 
00349   diostreamudp = (DIOSTREAMUDP*)GEN_DIOFACTORY.CreateStreamIO(diostreamudpcfg);
00350   if(diostreamudp)
00351     {
00352       if(diostreamudp->Open())
00353         {
00354           if(checkavailableNMS)
00355             {
00356               //--------------------------------------------
00357               // Write something to received a ICMP result.
00358               // and check if something is the other side.
00359 
00360               status = diostreamudp->WaitToConnected(3);
00361               if(status)
00362                 {
00363                   XBYTE data[] = { 0xAA,0x55 };
00364                   diostreamudp ->Write(data,sizeof(data));
00365 
00366                   status = diostreamudp->WaitToDisconnected(3);
00367                 }
00368 
00369             } else status = true;
00370         }
00371     }
00372 
00373   if(!status) Close();
00374 
00375   return status;
00376 }
