00636 bool DIOSNMP::Get(DIOSNMP_OPERATION operation, XCHAR* community, XCHAR* OIDstr, DIOSNMP_XBER& xberout)
00637 {
00638   #define MAX_READBUFFER  2048
00639 
00640   if(!diostreamudpcfg)  return false;
00641   if(!diostreamudp)     return false;
00642   
00643   XBUFFER       xbuffer;
00644   XBUFFER       xbufferdata;  
00645   XDWORD        number = 0;
00646   DIOSNMP_XBER  xber;
00647   DIOSNMP_XBER  value;
00648   DIOSNMP_XBER  bindings;
00649   DIOSNMP_XBER  request;
00650   DIOSNMP_XBER  data_next_request;
00651   DIOSNMP_XBER  header;  
00652 
00653   // ----------------------------------------------------------------
00654   // Get-Next 
00655 
00656   xber.SetOID(OIDstr);                                        value.Sequence_AddTo(xber);          // OID
00657   xber.SetNULL();                                             value.Sequence_AddTo(xber);          // NULL  
00658 
00659   XRAND* GEN_XFACTORY_CREATE(xrand, CreateRand())
00660   if(xrand)
00661     {
00662       number = xrand->Max(65000);
00663       GEN_XFACTORY.DeleteRand(xrand);
00664     }
00665 
00666   xber.SetINTEGER(number);                                    bindings.Sequence_AddTo(xber);        // Request-ID 
00667   xber.SetINTEGER(0);                                         bindings.Sequence_AddTo(xber);        // Error-status 
00668   xber.SetINTEGER(0);                                         bindings.Sequence_AddTo(xber);        // Error-index
00669 
00670   request.Sequence_AddTo(value);
00671   bindings.Sequence_AddTo(request);  
00672   
00673   data_next_request.Sequence_AddCommandTo(DIOSNMP_OPERATION_GETNEXTREQUEST, bindings); 
00674 
00675   xber.SetINTEGER(version);                                   header.Sequence_AddTo(xber);        // Version 
00676   xber.SetOCTETSTRING(community);                             header.Sequence_AddTo(xber);        // Community   
00677   
00678   header.Sequence_AddTo(data_next_request);     
00679 
00680   header.GetDump(xbuffer);
00681 
00682   XDWORD bw = diostreamudp->Write(xbuffer.Get(), xbuffer.GetSize());
00683   if(bw!= xbuffer.GetSize()) return false;
00684 
00685   xbuffer.Empty();
00686   xbuffer.Resize(MAX_READBUFFER);
00687 
00688   XTIMER* GEN_XFACTORY_CREATE(timeout, CreateTimer())
00689   if(!timeout) return false;
00690 
00691   do{ XDWORD sizeread  = diostreamudp->GetInXBuffer()->GetSize();
00692       if(sizeread)
00693         {
00694           XDWORD br = diostreamudp->Read(xbuffer.Get(), sizeread);
00695           if(br)
00696             {
00697               timeout->Reset();
00698               xbufferdata.Add(xbuffer.Get(), br);  
00699             }      
00700         }
00701       
00702       if(timeout->GetMeasureSeconds() > 2) break; 
00703 
00704     } while(1);
00705 
00706   GEN_XFACTORY.DeleteTimer(timeout);
00707 
00708   if(xbufferdata.GetSize())
00709     {
00710       xberout.SetTagType(xbufferdata.Get()[0]);
00711       xberout.SetSize(xbufferdata.Get()[1]);  
00712       xberout.GetData()->Add(&xbufferdata.Get()[2], xbufferdata.GetSize());
00713     }
00714   
00715   return xbufferdata.GetSize()?true:false;
00716 }
