00111 bool DIOSPIOLEDDISPLAYSSD1306::IniDevice()
00112 {
00113   if(!DIODEVICESPI::IniDevice()) return false;
00114 
00115   diostreamcfg->SetSPIMode(DIOSTREAMSPI_MODE_3);
00116   diostreamcfg->SetNBitsWord(8);
00117   diostreamcfg->SetSpeed(16*1000*1000);
00118   diostreamcfg->SetDelay(0);
00119   diostreamcfg->SetIsOnlyWrite(true);
00120  
00121   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) != DIOGPIO_ID_NOTDEFINED)
00122     {
00123       GEN_DIOGPIO.SetMode(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)  , DIOGPIO_MODE_OUTPUT);
00124       GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) , true);
00125     }
00126 
00127   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC) != DIOGPIO_ID_NOTDEFINED)
00128     {
00129       GEN_DIOGPIO.SetMode(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)     , DIOGPIO_MODE_OUTPUT);
00130       GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)    , true);
00131     }
00132 
00133   if(diostream->Open())
00134     {
00135       if(diostream->WaitToConnected(timeout))
00136         {
00137           Reset();
00138 
00139           if(!Initializate())  return false;
00140 
00141           return DIODEVICE::Ini();
00142         }
00143     }
00144 
00145   return false;
00146 }
