00160 bool DIOSPIOLEDDISPLAYSSD1306::Clear(XBYTE color)
00161 {
00162   XBYTE nblocks;
00163 
00164   nblocks = (width >> 4) * (height >> 3);
00165 
00166   // Set columns
00167   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_COLUMN_ADDRESS_SET);     // 0x21
00168   SendCommand(0x00);                                                    // Start
00169   SendCommand(width - 1);                                               // End
00170 
00171   //Set rows
00172   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_PAGE_ADDRESS_SET);       // 0x22
00173   SendCommand(0x00);                                                    // Start
00174   SendCommand(height - 1);                                              // End
00175 
00176   XBYTE buffer[16];
00177 
00178   memset(buffer, color, 16);
00179 
00180   //Send buffer
00181   for(XBYTE c=0; c<nblocks; c++)
00182     {
00183       SendData(buffer, 16);
00184     }
00185 
00186   return true;
00187 }
