00201 bool DIOSPIOLEDDISPLAYSSD1306::Update(XBYTE* buffer)
00202 {  
00203   XBYTE nblocks;
00204 
00205   nblocks = (width >> 4) * (height >> 3);
00206 
00207   // Set columns
00208   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_COLUMN_ADDRESS_SET);      // 0x21
00209   SendCommand(0x00);                                                    // Start
00210   SendCommand(width - 1);                                               // End
00211 
00212   //Set rows
00213   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_PAGE_ADDRESS_SET);        // 0x22
00214   SendCommand(0x00);                                                    // Start
00215   SendCommand(height - 1);                                              // End
00216 
00217   for(XBYTE c=0; c<nblocks; c++)
00218     {
00219       SendData(buffer+(c*16), 16);
00220     }
00221 
00222   return true;
