00291 bool DIOSPIOLEDDISPLAYSSD1306::Initializate()
00292 {
00293   Reset();
00294 
00295   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_OFF))                          return false;     // 0xAE
00296   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_CLOCK_DIV_RATIO_SET))          return false;     // 0xD5
00297   if(!SendCommand(0xF0))                                                                  return false;     // Suggest ratio
00298   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_MULTIPLEX_RADIO_SET))                  return false;     // 0xA8
00299   if(!SendCommand((64 - 1)))                                                              return false;     // Display_Height - 1
00300 
00301   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_OFFSET_SET))                   return false;     // 0xD3 + No offset
00302   if(!SendCommand(00))                                                                    return false;     // No offset
00303   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_CHARGE_PUMP_SET))                      return false;     // 0x8D
00304   if(!SendCommand(0x14))                                                                  return false;     // Enable charge pump
00305   if(!SendCommand((DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_START_LINE_SET | 0x00)))      return false;     // 0x40 | Start line
00306   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_MEMORY_ADDRESSING_SET))                return false;     // 0x20
00307   if(!SendCommand(0x00))                                                                  return false;     // Horizontal Addressing - Operate like KS0108
00308   if(!SendCommand((DIOSPIOLEDDISPLAYSSD1306_COMMAND_SEGMENT_REMAP_SET | 0x01)))           return false;     // 0xA0 - Left towards Right
00309   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_COM_OUTPUT_SCAN_DEC))                  return false;     // 0xC8 - Up towards Down
00310   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_COM_PINS_SET))                         return false;     // 0xDA
00311 //if(!SendCommand(0x12))                                                                  return false;     // Sequential COM pin configuration
00312 
00313   if((width == 128) && (height == 64)) 
00314     {
00315       if(!SendCommand(0x12))                             return false;     // Sequential COM pin configuration
00316     }
00317   if((width == 128) && (height == 32)) 
00318     {
00319       if(!SendCommand(0x02))                             return false;     // Sequential COM pin configuration
00320     }
00321   if((width ==  96) && (height == 16)) 
00322     {
00323       if(!SendCommand(0x02))                             return false;     // Sequential COM pin configuration
00324     }
00325 
00326   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_CONSTRAST_SET))                        return false;     // 0x81
00327   if(!SendCommand(0xFF))                                                                  return false;     // 0xFF
00328   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_PRECHARGE_PERIOD_SET))                 return false;     // 0xD9
00329   if(!SendCommand(0xF1))                                                                  return false;     // 0xF1
00330 
00331   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_VCOMH_DESELECT_LEVEL_SET))             return false;     // 0xDB
00332   if(!SendCommand(0x20))                                                                  return false;     // 0x20
00333 
00334   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_ALL_ON_RESUME))                return false;     // 0xA4
00335   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_NORMAL))                       return false;     // 0xA6
00336   if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_SCROLL_DEACTIVATE))                    return false;     // 0x2E
00337 
00338 //if(!SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_DISPLAY_ON))                           return false;     // 0xAF
00339 
00340 //Activate(false);
00341 
00342   Clear(0x00);
00343 
00344   Activate(true);
00345 
00346   return true;
00347 }
