00359 bool DIOSPIOLEDDISPLAYSSD1306::Reset()
00360 {
00361   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) == DIOGPIO_ID_NOTDEFINED)   return false;
00362   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)    == DIOGPIO_ID_NOTDEFINED)   return false;
00363             
00364   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC), false);
00365   GEN_XSLEEP.MilliSeconds(10); 
00366   
00367   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), false);
00368   GEN_XSLEEP.MilliSeconds(10);
00369 
00370   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), true);
00371   GEN_XSLEEP.MilliSeconds(10);
00372 
00373   return true;
00374 }
