00296 bool DIOSPIOLEDDISPLAYSSD1331::Reset()
00297 {
00298   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) == DIOGPIO_ID_NOTDEFINED)  return false;
00299   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)    == DIOGPIO_ID_NOTDEFINED)  return false;
00300             
00301   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC), false);
00302   GEN_XSLEEP.MilliSeconds(10); 
00303   
00304   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), false);
00305   GEN_XSLEEP.MilliSeconds(10);
00306 
00307   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), true);
00308   GEN_XSLEEP.MilliSeconds(10);
00309 
00310   return true;
