00375 bool DIOSPITFTDISPLAYILI9341::TFT_RotateRGB(int rotate, XBYTE bgr)
00376 {
00377   #define MEM_Y   (7) /* MY row address order */
00378   #define MEM_X   (6) /* MX column address order */
00379   #define MEM_V   (5) /* MV row / column exchange */
00380   #define MEM_L   (4) /* ML vertical refresh order */
00381   #define MEM_H   (2) /* MH horizontal refresh order */
00382   #define MEM_BGR (3) /* RGB-BGR Order */
00383 
00384   bool status = false;
00385 
00386   switch(rotate)
00387     {
00388       case    0 : status = WriteRegister(0x36, 1, (1 << MEM_X) | (bgr << MEM_BGR));                               break;
00389       case   90 : status = WriteRegister(0x36, 1, (1 << MEM_Y) | (1 << MEM_X) |(1 << MEM_V) | (bgr << MEM_BGR));  break;
00390       case  180 : status = WriteRegister(0x36, 1, (1 << MEM_Y) | (bgr << MEM_BGR));                               break;
00391       case  270 : status = WriteRegister(0x36, 1, (1 << MEM_V) | (1 << MEM_L) | (bgr << MEM_BGR));                break;
00392       default   : break;
00393     }
00394 
00395   return status;
00396 }
